import React from 'react';
import './App.css';
import SiteName from './components/site-name';
import cities from './components/cities';
import myNews from './components/myNews';

const gettingWeather = async () => {
  const api_url = await fetch(`https://www.metaweather.com/api/location/${cities}/`);
};

class News extends React.Component {
  render() {
    const newsTemplate = this.props.data.map(function(item, index) {
      return (
          <div key={index}>
            <p className="news__author"><b>{item.author}:</b></p>
            <p className="news__text">{item.text}</p>
          </div>
      )
    });

    console.log(newsTemplate);

    return (
        <div className="news">
          {newsTemplate}
        </div>
    )
  }
}

const Comments = () => {
  return <p>Нет новостей - комментировать нечего.</p>;
};

function App() {
  return (
      <React.Fragment>
        <SiteName />
        <News data={myNews} />
        <Comments />
      </React.Fragment>
  );
}

export default App;
