import React from 'react';
import logo from './logo.svg';
import './App.css';
import SiteName from 'components/SiteName';

// const SiteName = () => {
//   return <h1>Погода на сегодня</h1>;
// };

const API_KEY = "";

const cities = [
  {
    'name':'Moscow',
    'location': '2122265'
  },
  {
    'name':'Saint-Peterburg',
    'location': '2123260'
  },
  {
    'name':'Kiev',
    'location': '924938'
  },
  {
    'name':'Minsk',
    'location': '834463'
  }
];

// const gettingWeather = async () => {
//   const api_url = await fetch(`https://www.metaweather.com/api/location/${cities: location}/`);
// };

const myNews = [
  {
    author: 'Саша Печкин',
    text: 'В четверг, четвертого числа...'
  },
  {
    author: 'Просто Вася',
    text: 'Считаю, что $ должен стоить 35 рублей!'
  },
  {
    author: 'Max Frontend',
    text: 'Прошло 2 года с прошлых учебников, а $ так и не стоит 35'
  },
  {
    author: 'Гость',
    text: 'Бесплатно. Без смс, про реакт, заходи - https://maxpfrontend.ru'
  }
];

class News extends React.Component {
  render() {
    const newsTemplate = this.props.data.map(function(item, index) {
      return (
          <div key={index}>
            <p className="news__author">{item.author}:</p>
            <p className="news__text">{item.text}</p>
          </div>
      )
    });

    console.log(newsTemplate);

    return (
        <div className="news">
          {newsTemplate}
        </div>
    )
  }
}

const Comments = () => {
  return <p>Нет новостей - комментировать нечего.</p>;
};

function App() {
  return (
      <React.Fragment>
        <SiteName />
        <News data={myNews} />
        <Comments />
      </React.Fragment>
  );
}

export default App;
